refentry.refsect1:
  &reftitle.parameters;




     value



       要过滤的内容。注意：标量值在过滤前，会被转换成字符串。



    &filter.param.filter;

     options



       一个选项的关联数组，或者按位区分的标示。如果过滤器接受选项，可以通过数组的 "flags" 下标去提供这些标示。
       对于回调型的过滤器，应该传入 callable。
       这个回调函数必须接受一个参数（即待过滤的值），并且返回一个在过滤/净化后的值。





<?php
// 对于接受选项的过滤器，请使用此格式
$options = array(
    'options' => array(
        'default' => 3, // 过滤器失败时返回的值
        // 可以继续写过滤器接收其它选项
        'min_range' => 0
    ),
    'flags' => FILTER_FLAG_ALLOW_OCTAL,
);
$var = filter_var('0755', FILTER_VALIDATE_INT, $options);
// 对于仅接受标志的过滤器，您可以直接将其传递
$var = filter_var('oops', FILTER_VALIDATE_BOOLEAN, FILTER_NULL_ON_FAILURE);
// 对于仅接受标志的过滤器，您还可以将其作为数组传递
$var = filter_var('oops', FILTER_VALIDATE_BOOLEAN,
                  array('flags' => FILTER_NULL_ON_FAILURE));
// 回调类型过滤器
function foo($value)
{
    // 预期值: Surname, GivenNames
    if (strpos($value, ", ") === false) return false;
    list($surname, $givennames) = explode(", ", $value, 2);
    $empty = (empty($surname) || empty($givennames));
    $notstrings = (!is_string($surname) || !is_string($givennames));
    if ($empty || $notstrings) {
        return false;
    } else {
        return $value;
    }
}
$var = filter_var('Doe, Jane Sue', FILTER_CALLBACK, array('options' => 'foo'));
?>

  &reftitle.examples;



    一个 filter_var 的示例


<?php
var_dump(filter_var('bob@example.com', FILTER_VALIDATE_EMAIL));
var_dump(filter_var('http://example.com', FILTER_VALIDATE_URL, FILTER_FLAG_PATH_REQUIRED));
?>


    &example.outputs;


string(15) "bob@example.com"
bool(false)
